mapper.update:
UPDATE BOOKMARK
SET 
	bookmark_memo = #{bookmarkMemo}
WHERE
	board_no = #{boardNo}
	AND
	user_no = #{userNo}

mapper.insert:
INSERT INTO BOOKMARK (
	user_no,
	board_no
) VALUES (
	#{userNo},
	#{boardNo}
)

mapper.delete:
DELETE FROM BOOKMARK 
WHERE
	user_no = #{userNo}
	AND
	board_no =#{boardNo}

mapper.select:
SELECT BOARD.board_no, BOARD.board_no as number, BOARD.user_no, BOARD.category_no, BOARD.board_title, BOARD.board_content_summary, BOARD.board_date, BOARD.board_content, CATEGORY.category_item, USER.user_name, USER.user_id, , ,
FROM BOARD JOIN CATEGORY ON BOARD.category_no = CATEGORY.category_no JOIN USER ON BOARD.user_no = USER.user_no
WHERE BOARD.user_no = #{userNo}
JOIN BOOKMARK ON BOARD.board_no = BOOKMARK.board_no WHERE BOOKMARK.user_no = #{userNo}
ORDER BY board_view_cnt desc
ORDER BY comment_count desc
ORDER BY BOARD.board_no desc
LIMIT #{startBoardNo}, #{perPageNum}

SELECT count(board_no)
FROM BOARD
WHERE user_no = #{userNo}

mapper.insert:
INSERT IGNORE INTO BOARD_VIEW (
	board_no, 
	user_no
) VALUES (
	#{boardNo},
	#{userNo}
)
