mapper.update:
UPDATE BOOKMARK
SET 
	bookmark_memo = #{bookmarkMemo}
WHERE
	board_no = #{boardNo}
	AND
	user_no = #{userNo}

mapper.insert:
INSERT INTO BOOKMARK (
	user_no,
	board_no
) VALUES (
	#{userNo},
	#{boardNo}
)

mapper.select:
SELECT BOARD.board_no, BOARD.category_no, CATEGORY.category_item, BOARD.board_title, BOARD.board_content_summary, BOARD.board_date, BOARD.board_view_cnt, count(comment_no) as comment_count FROM BOARD
WHERE BOARD.user_no = #{userNo}
AND
AND
group by board_no ORDER BY BOARD.board_no desc, BOARD.board_date desc limit #{startBoardNo}, #{perPageNum}

JOIN BOOKMARK ON BOARD.board_no = BOOKMARK.board_no WHERE BOOKMARK.user_no = #{userNo}
AND
AND
group by board_no ORDER BY BOARD.board_no desc, BOARD.board_date desc limit #{startBoardNo}, #{perPageNum}

SELECT COUNT(board_no) FROM BOARD WHERE BOARD.user_no = #{userNo}
AND
AND

SELECT COUNT(board_no) FROM BOOKMARK WHERE user_no = #{userNo}
AND
AND
