mapper.select:
SELECT C.contact_no, C.contact_no as no, C.user_no, C.user_id, C.contact_title, C.contact_content, C.contact_password, C.contact_date, C.contact_comment_count FROM ( SELECT contact_no, contact_no as no, CONTACT.user_no, USER.user_id, contact_title, contact_content, contact_password, contact_date, (select count(1) from CONTACT_COMMENT where CONTACT_COMMENT.contact_no = no) as contact_comment_count FROM CONTACT JOIN USER ON USER.user_no = CONTACT.user_no ORDER BY CONTACT.contact_no desc
LIMIT #{startBoardNo}, #{perPageNum}
) C
WHERE contact_comment_count = 0 LIMIT #{startBoardNo}, #{perPageNum}

SELECT COUNT(C.contact_no)
FROM ( SELECT contact_no, contact_no as no, (SELECT count(1) FROM CONTACT_COMMENT WHERE CONTACT_COMMENT.contact_no = no) as contact_comment_count FROM CONTACT ) C WHERE C.contact_comment_count = 0;
FROM CONTACT as C

mapper.insert:
INSERT INTO CONTACT_COMMENT (
	user_no,
	contact_no,
	contact_comment_content
) VALUES (
	#{userNo},
	#{contactNo},
	#{contactCommentContent}
)
