mapper.insert:
INSERT INTO NOTICE (
	notice_title,
	notice_content,
	user_no
) VALUES (
	#{noticeTitle},
	#{noticeContent},
	#{userNo}
)

mapper.update:
UPDATE NOTICE
SET 
	notice_title = #{noticeTitle},
	notice_content = #{noticeContent}
WHERE
	notice_no = #{noticeNo}

mapper.select:
SELECT count(1)
FROM NOTICE
WHERE notice_no = #{noticeNo}
